const Sidebar = ({notes, addNote, activeNote, setActiveNote}) => {
    const sortedNotes = notes.sort((one, two) => two.lastModified - one.lastModified);

    return (
        <>
        <div className="app-sidebar">
            <div id="sidebar-header">
                <h3>Notes</h3>
                <button className="add-note" onClick={addNote}>&#43;</button>
            </div>

            <div className="sidebar-notes">
            {sortedNotes.map(({ id, title, content, lastModified}, i) => 
                <div 
                className={`sidebar-note ${id === activeNote && "active"}`}
                onClick={() => setActiveNote(id)}>

                <div className="note-title-side">
                    <strong>{title}</strong>
                </div>

                <small className="note-date">
                    {new Date(lastModified).toLocalDateString("en-GB", {
                        hour: "2-digit",
                        minute: "2-digit",
                    })}
                </small>

                <p>{content && content.substr(0, 100) + "..."}</p>

                </div>


            )};
            </div>
        </div>

        </>
    );
}

export default Sidebar;
