import { v4 as uuid } from "uuid";
import { useState, useEffect } from "react";
import { Outlet, Link} from "react-router-dom";

function Layout() {
  const history = useHistory();
  const [toggle, setToggle] = useState(true);

  const [notes, setNotes] = useState(
    localStorage.notes ? JSON.parse(localStorage.notes) : []
  );

  //localStorage.clear();

  const [activeNote, setActiveNote] = useState(false);

  useEffect(() => {
    localStorage.setItem("notes", JSON.stringify(notes));
  }, [notes]);

  const onAddNote = () => {
    const newNote = {
      id: uuid(),
      title: "Untitled",
      content: "",
      lastModified: Date.now(),
    };

    setNotes([newNote, ...notes]);
    setActiveNote(newNote.id);
  };

  const onUpdateNote = (updatedNote) => {
    const updatedNotesArray = notes.map((note) => {
      if(note.id === activeNote){
        return updatedNote
      }

      return note
    });

    setNotes(updatedNotesArray);
  }

  /*const onDeleteNote = (noteId) => {
    setNotes(notes.filter(({ id }) => id !== noteId));
  };*/
  /*const handleDeleteNote = (noteId) => {
    const notes = JSON.parse(localStorage.getItem("notes")) || [];
    const index = notes.findIndex((n) => n.id === noteId);
    if (index >= 0) {
      notes.splice(index, 1);
      localStorage.setItem("notes", JSON.stringify(notes));
      const remainingNotes = notes.filter((n) => n.id !== noteId);
      if (remainingNotes.length > 0) {
        const nextNote = remainingNotes[0];
        history.push(`/notes/${nextNote.id}`);
      } else {
        history.push("/notes");
      }
    }
  };*/

  const getActiveNote = () => {
    return notes.find(({ id }) => id === activeNote);
  };

  return (
    <>
      <div className="header">
        <button id="menu" onClick={() => setToggle(!toggle)}>
          &#9776;
        </button>
        <div id="title">
          <h2>Lotion</h2>
          <h6>Like Notion, but worse</h6>
        </div>
      </div>

      <div className="main">
        {toggle && (
          <>
            <div className="sidebar">
              <div id="sidebar-header">
                <h3>Notes</h3>
                <button className="add-note" onClick={() => onAddNote()}>
                  &#43;
                </button>
              </div>

              <div className="sidebar-notes">
                {notes.length === 0 ? (
                  <p>No notes yet.</p>
                ) : (
                  notes.map((note) => (
                    <Link to={`/notes/${note.id}/edit`} key={note.id}>
                      <div className={`sidebar-note ${note.id === activeNote && "active"}`} onClick={() => setActiveNote(note.id)}>
                        <strong>{note.title}</strong>
                        <small>Last modified {note.lastModified}</small>
                        {note.content && note.content.toString().replace( /(<([^>]+)>)/ig, '').substring(0, 100) + "..."}

                      </div>
                    </Link>
                  ))
                )}
              </div>

            </div>
          </>
        )}

        <Outlet context={[handleDeleteNote, getActiveNote, notes]} />
      </div>
    </>
  );
}

export default Layout;
