import { useParams } from "react-router-dom";

function Note() {
  const { id } = useParams();
  const notes = JSON.parse(localStorage.getItem("notes"));
  const note = notes.find((note) => note.id === id);

  if (!note) {
    return <p>Note not found</p>;
  }

  return (
    <div>
      <h2>{note.title}</h2>
      <p>{note.content}</p>
    </div>
  );
}

export default Note;
