    const [notes, setNotes] = useState(
        localStorage.notes ? JSON.parse(localStorage.notes) : []
      );

      const [activeNote, setActiveNote] = useState(false);

      useEffect(() => {
        localStorage.setItem("notes", JSON.stringify(notes));
      }, [notes]);

