        const newNote = {
          id: uuid(),
          title: "Untitled Note",
          content: "",
          lastModified: Date.now(),
        };

        setNotes([newNote, ...notes]);
        setActiveNote(newNote.id);
      };

      const deleteNote = (noteId) => {
        setNotes(notes.filter(({ id }) => id !== noteId));
      };

      const getActiveNote = () => {
        return notes.find(({ id }) => id === activeNote)
      };

