import React from "react";
import { useParams, useContext } from "react-router-dom";
import ReactQuill from "react-quill";

function NoteEditor({ activeNote, onUpdateNote }) {
  const [title, setTitle] = useState(activeNote.title);
  const [content, setContent] = useState(activeNote.content);

  const onTitleChange = (event) => {
    setTitle(event.target.value);
  };

  const onContentChange = (value) => {
    setContent(value);
    onUpdateNote({
      ...activeNote,
      content: value,
      lastModified: Date.now(),
    });
  };

  return (
    <div className="note-editor">
      <input type="text" value={title} onChange={onTitleChange} />
      <ReactQuill value={content} onChange={onContentChange} />
    </div>
  );
}

function NoteViewer({ activeNote }) {
  return (
    <div className="note-viewer">
      <h2>{activeNote.title}</h2>
      <p>Last modified: {activeNote.lastModified}</p>
      <div dangerouslySetInnerHTML={{ __html: activeNote.content }} />
    </div>
  );
}

function Note() {
  const { noteId } = useParams();
  const { onDeleteNote, getActiveNote } = useContext(NotesContext);

  const activeNote = getActiveNote();

  const onUpdateNote = (updatedNote) => {
    notesDispatch({ type: "UPDATE_NOTE", note: updatedNote });
  };

  const onDeleteClick = () => {
    onDeleteNote (activeNote.id);
  };

  if (!activeNote) {
    return <div>No note selected</div>;
  }

  return (
    <div className="note">
      <button onClick={onDeleteClick}>Delete</button>
      {activeNote ? (
        <NoteEditor activeNote={activeNote} onUpdateNote={onUpdateNote} />
      ) : (
        <NoteViewer activeNote={activeNote} />
      )}
    </div>
  );
}

export { NoteEditor, NoteViewer, Note };
