import Main from "./Main";
import Sidebar from "./Sidebar";
import App from "./App";
import { v4 as uuid } from "uuid";
import { useState, useEffect } from "react";
import { Outlet } from "react-router-dom";

function Layout(){
    const [toggle, setToggle] = useState(true)

    const [notes, setNotes] = useState(
        localStorage.notes ? JSON.parse(localStorage.notes) : []
      );

      const [activeNote, setActiveNote] = useState(false);

      useEffect(() => {
        localStorage.setItem("notes", JSON.stringify(notes));
      }, [notes]);

      const onAddNote = () => {
        const newNote = {
          id: uuid(),
          title: "Untitled",
          content: "",
          lastModified: Date.now(),
        };

        setNotes([newNote, ...notes]);
        setActiveNote(newNote.id);
      };

      const onDeleteNote = (noteId) => {
        setNotes(notes.filter(({ id }) => id !== noteId));
      };

      const getActiveNote = () => {
        return notes.find(({ id }) => id === activeNote)
      };

    return(
        <>
                <div className="header">
                    <button id="menu" onClick={() => setToggle(!toggle)}>&#9776;</button>
                    <div id="title">
                        <h2>Lotion</h2>
                        <h6>Like Notion, but worse</h6>
                    </div>
                </div>

            <body className="main">
                {toggle && (
                    <>
                    <div className="sidebar">
                        <div id="sidebar-header">
                            <h3>Notes</h3>
                            <button className="add-note" onClick={() => onAddNote()}>&#43;</button>
                        </div>

                        <div className="sidebar-notes">
                            {notes.map((note) => ( 
                                <div className="sidebar-note" key={note.id}>
                                    <strong>{note.title}</strong>
                                    <small>{" "}
                                    {new Date(note.lastModified).toLocalDateString("en-GB", {
                                        hour: "2-digit",
                                        minute: "2-digit",
                                    })}</small>*/
                                    <p>{note.content && note.content.substr(0, 100) + "..."}</p>
                                </div>
                            ))}

                        </div>
                    </div>
                    </>
                )}

                <Outlet context={[onDeleteNote]}/>
            </body>


        </>

    );
}

export default Layout;
