import { useEffect, useState } from "react";
import { useOutletContext, useParams } from "react-router-dom";
import DateTimeP from 'react-datetime-picker';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';

function Edit(){
    const [ getActiveNote, notes] = useOutletContext(); 
    const { id } = useParams();
    const [note, setNote] = useState(null);

    useEffect(() => {
        const notes = JSON.parse(localStorage.getItem("notes")) || [];
        const index = notes.findIndex((n) => n.id === noteId);
        if (index >= 0) {
          setNote(notes[index]);
        }
      }, [noteId]);

      useEffect(() => {
        if (notes) {
          const index = notes.findIndex((n) => n.id === noteId);
          if (index >= 0) {
            setNote(notes[index]);
          }
        }
      }, [noteId, notes]);

      useEffect(() => {
        if (note) {
          const notes = JSON.parse(localStorage.getItem("notes")) || [];
          const index = notes.findIndex((n) => n.id === note.id);
          if (index >= 0) {
            notes[index] = note;
          } else {
            notes.push(note);
          }
          localStorage.setItem("notes", JSON.stringify(notes));
        }
      }, [note]);
    /*useEffect(() => {
        const notes = JSON.parse(localStorage.getItem("notes")) || [];
        const index = notes.findIndex((n) => n.id === id);
        if(index >= 0){
            setNote(notes[index]);
        }
    }, [id]);

    useEffect(() => {
        if (notes) {
            const index = notes.findIndex((n) => n.id === id);
            if (index >= 0) {
                setNote(notes[index]);
            }
        }
    }, [id, notes]);

    useEffect(() => {
        if (note) {
            const notes = JSON.parse(localStorage.getItem("notes")) || [];
            const index = notes.findIndex((n) => n.id === note.id);
            if (index >= 0) {
                notes[index] = note;
            } else {
                notes.push(note);
            }
            localStorage.setItem("notes", JSON.stringify(notes));
        }
    }, [note]);*/
    /*useEffect(() => {
        const notes = JSON.parse(localStorage.getItem("notes")) || [];
        const index = notes.findIndex((n) => n.id === id);
        if(index >= 0){
            setNote(notes[index]);
        }
    }, [id]);*/

    /*useEffect(() => {
        if(notes){
            const index = notes.findIndex((n) => n.id === id);
            if(index >= 0){
                setNote(notes[index]);
            }
        }
    }, [id, notes]);*/
    /*useEffect(() => {
        if (note) {
          const notes = JSON.parse(localStorage.getItem("notes")) || [];
          const index = notes.findIndex((n) => n.id === note.id);
          if (index >= 0) {
            notes[index] = note;
          } else {
            notes.push(note);
          }
          localStorage.setItem("notes", JSON.stringify(notes));
        }
      }, [note]);*/

    const handleTitleChange = (event) => {
        setNote({
            ...note,
            title: event.target.value,
            lastModified: new Date().toLocaleString(),
        });
    };

    const handleContentChange = (content) => {
        setNote({
            ...note,
            content,
            lastModified: new Date().toLocaleString(),
        });
    };

    /*const handleSave = () => {
        const notes = JSON.parse(localStorage.getItem("notes")) || [];
        const index = notes.findIndex((n) => n.id === note.id);
        if(index >= 0){
            notes[index] = note;
        }else{
            notes.push(note);
        }
        localStorage.setItem("notes", JSON.stringify(notes));
        setIsEditMode(false);
    }*/
    const handleSave = () => {
        const notes = JSON.parse(localStorage.getItem("notes")) || [];
        const index = notes.findIndex((n) => n.id === note.id);
        if (index >= 0) {
            // Update the existing note in the sidebar list with the new content
            notes[index] = note;
        } else {

            notes.push(note);
        }
        localStorage.setItem("notes", JSON.stringify(notes));
        setIsEditMode(false);

        // Set the new note as the active note in the outlet context
        getActiveNote(note);
    }

    const handleDelete = () => {
        const notes = JSON.parse(localStorage.getItem("notes")) || [];
        const index = notes.findIndex((n) => n.id === note.id);
        if (index >= 0) {
          const confirmation = window.confirm("Are you sure you want to delete this note?");
          if (confirmation) {
            notes.splice(index, 1);
            localStorage.setItem("notes", JSON.stringify(notes));
            const nextNoteIndex = index === notes.length ? index - 1 : index;
            const nextNoteId = nextNoteIndex >= 0 ? notes[nextNoteIndex].id : '';
            setNoteId(nextNoteId);
          }
        }
      };

    /*const handleDelete = () => {
        const notes = JSON.parse(localStorage.getItem("notes")) || [];
        const index = notes.findIndex((n) => n.id === note.id);
        if(index >= 0){
            notes.splice(index, 1);
            localStorage.setItem("notes", JSON.stringify(notes));
            const nextNoteIndex = index === notes.length ? index - 1 : index;
            const nextNoteId = nextNoteIndex >= 0 ? notes[nextNoteIndex].id : '';
            window.location.replace(`/notes/${nextNoteId}`);
        }
    };*/

    const [isEditMode, setIsEditMode] = useState(true);

    return(
        <>
        {notes && notes.length > 0 ? (
                    <div className="main-notes">
                    <div className="main-header">
                        {isEditMode ? (
                            <div className="header-left">
                                <div className="header-title">
                                    <input type="text" value={note?.title} onChange={handleTitleChange}></input>
                                </div>
                                <div >
                                    <DateTimeP value={new Date()} onChange={() => {}}/>
                                </div>
                            </div>
                        ) : (
                            <div className="header-left">
                            <div className="header-title">
                                <p type="text" value={note.title} onChange={handleTitleChange}></p>
                            </div>
                            <div>
                                <DateTimeP value={new Date()} onChange={() => {}} readOnly />
                            </div>
                        </div>
                        )}

                        <div className="header-right">
                            {isEditMode ? (
                                <button className="header-buttons" onClick={handleSave}>Save</button>
                            ) : (
                                <button className="header-buttons" onClick={() => setIsEditMode(true)}>Edit</button>
                            )}
                            <button className="header-buttons" onClick={() => handleDelete(note.id)}>Delete</button>
                        </div>
                    </div>

                    {isEditMode ? (
                        <div className="main-quill">
                            <ReactQuill theme="snow" value={note?.content} onChange={handleContentChange}/>
                        </div>
                    ) : (
                        <div className="main-note">
                            <p>{note.content.toString().replace( /(<([^>]+)>)/ig, '')}</p>
                        </div>
                    )}
                </div>) : (
                    <div>No notes yet.</div>
        )}

        </>
    )
}

export default Edit;
