import { useState } from "react";
import { useOutletContext } from "react-router-dom";
import DateTimeP from 'react-datetime-picker';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';

function Edit(){
    const [onDeleteNote, activeNote, onUpdateNote] = useOutletContext();

    const onEditField = (key, value) => {
        onUpdateNote({
            id: activeNote.id,
            [key]: value,
            lastmodified: Date.now(),
        });

    }

    function Quill(){
        const [value, setValue] = useState('');

        return <ReactQuill theme="snow" value={value} onChange={setValue} />;
    }

    return(
        <>
        <div className="main-notes">
            <div className="main-header">
                <div className="header-left">
                    <div className="header-title">
                        <input placeholder="Untitled" 
                        value={activeNote.title} 
                        onChange={(e) => onEditField("title", e.target.value)}></input>
                    </div>
                    <div >
                        <DateTimeP value={new Date()} onChange={() => {}}/>
                    </div>
                </div>

                <div className="header-right">
                    <button className="header-buttons">Save</button>
                    <button className="header-buttons" onClick={() => onDeleteNote(window.location.pathname.split('/')[2])}>Delete</button>
                </div>
            </div>

            <div className="main-quill">
                <Quill 
                    value={activeNote.content} 
                    onChange={(e) => onEditField("content", e.target.value)}/>
            </div>
        </div>
        </>
    )
}

export default Edit;
