import { useEffect, useState } from "react";
import { useOutletContext, useParams } from "react-router-dom";
import DateTimeP from 'react-datetime-picker';

function Edit(){
    const [onDeleteNote, getActiveNote, notes] = useOutletContext(); 
    const { id } = useParams();
    const [note, setNote] = useState(notes.find((note) => note.id === id));

    useEffect(() => {
        const notes = JSON.parse(localStorage.getItem("notes")) || [];
        const index = notes.findIndex((n) => n.id === id);
        if(index >= 0){
            setNote(notes[index]);
        }
    }, [id]);

    const handleTitleChange = (event) => {
        setNote({
            ...note,
            title: event.target.value,
            lastModified: new Date().toLocaleString(),
        });
    };

    const handleSave = () => {
        const notes = JSON.parse(localStorage.getItem("notes")) || [];
        const index = notes.findIndex((n) => n.id === note.id);
        if(index >= 0){
            notes[index] = note;
        }else{
            notes.push(note);
        }
        localStorage.setItem("notes", JSON.stringify(notes));
        setIsEditMode(false);
    }

    const [isEditMode, setIsEditMode] = useState(true);

    return(
        <>
        <div className="main-notes">
            <div className="main-header">
                <div className="header-left">
                    <div className="header-title">
                        <input type="text" value={note.title} onChange={handleTitleChange}></input>
                    </div>
                    <div >
                        <DateTimeP value={new Date()} onChange={() => {}}/>
                    </div>
                </div>

                <div className="header-right">
                    {isEditMode ? (
                        <button className="header-buttons" onClick={handleSave}>Save</button>
                    ) : (
                        <button className="header-buttons" onClick={() => setIsEditMode(true)}>Edit</button>
                    )}
                    <button className="header-buttons" onClick={() => onDeleteNote(window.location.pathname.split('/')[2])}>Delete</button>
                </div>
            </div>

            {isEditMode ? (
                <div className="main-quill">
                    <ReactQuill theme="snow" value={note.content} onChange={handleContentChange}/>
                </div>
            ) : (
                <div className="main-note">
                    <p>{note.content}</p>
                </div>
            )}
        </div>
        </>
    )
}

export default Edit;
